mapper.select:
SELECT @myLeft := LFT
  FROM MENU
 WHERE MENU_ID = #{up_menu_id};

mapper.update:
UPDATE MENU 
   SET RGT = RGT + 2 
 WHERE RGT > @myLeft;

UPDATE MENU 
   SET LFT = LFT + 2 
 WHERE LFT > @myLeft;

mapper.insert:

			SELECT MAX(MENU_ID) + 1 MENU_ID from MENU;

	INSERT INTO MENU(
		   MENU_ID
  		 , MENU_NM
		 , LFT
		 , RGT
		 , UP_MENU_ID
		 , UUID			
	)VALUES (
		   #{menu_id}
		 , #{menu_nm}
		 , @myLeft + 1
		 , @myLeft + 2
		 , #{up_menu_id}
		 , #{uuid}
	);

mapper.update:
UPDATE MENU 
   SET MENU_NM = #{menu_nm}
 WHERE MENU_ID = #{menu_id};

mapper.select:
 	SELECT PARENT.MENU_NM AS MENU_NM
      FROM MENU AS NODE
	     , MENU AS PARENT
	 WHERE NODE.LFT BETWEEN PARENT.LFT AND PARENT.RGT
	   AND NODE.MENU_ID = #{menu_id}
	   AND PARENT.MENU_NM != 'ROOT';

SELECT LFT, RGT
  FROM MENU
 WHERE MENU_ID = #{menu_id};

SELECT @myLeft := LFT , @myRight := RGT, @myWidth := RGT - LFT + 1 
  FROM MENU
 WHERE MENU_ID = #{menu_id};

mapper.delete:
DELETE 
  FROM MENU 
 WHERE LFT BETWEEN @myLeft AND @myRight;

mapper.update:
UPDATE MENU 
   SET RGT = RGT - @myWidth 
 WHERE RGT > @myRight;

UPDATE MENU 
   SET LFT = LFT - @myWidth 
 WHERE LFT > @myLeft;

UPDATE MENU 
   SET RGT = RGT -1 , LFT = LFT - 1
 WHERE LFT BETWEEN @myLeft AND @myRight;

UPDATE MENU
   SET RGT = RGT - 2
 WHERE RGT > @myRight;

UPDATE MENU
   SET LFT = LFT - 2
 WHERE LFT > @myRight;

mapper.delete:
	DELETE 
	  FROM FILE
	 WHERE FILE_PATH = #{file_path}
  	   AND FILE_NM = #{file_nm};
